CREATE TABLE BECA (ID BIGINT NOT NULL, DESCRIPCION VARCHAR(255), FECHAEMISION DATE, FECHAFIN DATE, NOMBRE VARCHAR(255), CUANTÍA BIGINT, PRIMARY KEY (ID))
CREATE TABLE CCJ (ID BIGINT NOT NULL, DESCRIPCION VARCHAR(255), DIRECCION VARCHAR(255), NACION VARCHAR(255), NOMBRE VARCHAR(255), REGION VARCHAR(255), TELEFONO VARCHAR(255), EMAIL VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE JOVEN_NIÑO (ID BIGINT NOT NULL, APELLIDO1 VARCHAR(255), APELLIDO2 VARCHAR(255), DESCRIPCION VARCHAR(255), FECHANACIMIENTO DATE, NACIONALIDAD VARCHAR(255), NOMBRE VARCHAR(255), CCJ_ID BIGINT, CP_ID BIGINT, PADRINO_ID BIGINT, PRIMARY KEY (ID))
